//copied from stackoverflow: http://stackoverflow.com/questions/138600/initializing-a-static-stdmapint-int-in-c/1730798#1730798
//Usage: std::map< int, int > mymap = create_static_map< int, int > (1,2) (3,4) (5,6);
template <typename T, typename U> class create_static_map
{
	/*
		todo: 2022-02-21
		this compiles and tests OK, but a recent check of the link
		indicated that tis class is obsolete / error-prone
		will fix this ASAP
	*/
private:
    std::map<T, U> m_map;
public:
    create_static_map(const T& key, const U& val) { m_map[key] = val; }
    create_static_map<T, U>& operator()(const T& key, const U& val) { m_map[key] = val; return *this; }
    operator std::map<T, U>() { return m_map; }
};

